import React from 'react';
import { Divider, Box, Typography, Paper } from '@mui/material';
import { Alert } from '../../types/alert';
import { getParameterIcon } from '../../utils/alertUtils';

interface AlertStatusOverviewProps {
  alerts: Alert[];
  lastUpdated: Date | null;
}

const AlertStatusOverview: React.FC<AlertStatusOverviewProps> = ({ alerts, lastUpdated }) => {
  // Stats calculation
  const triggeredAlerts = alerts.filter((alert) => alert.isTriggered);
  const normalAlerts = alerts.filter((alert) => !alert.isTriggered);
  const totalAlerts = alerts.length;
  
  // Group alerts by parameter
  const alertsByParameter = alerts.reduce((acc: {[key: string]: number}, alert) => {
    acc[alert.parameter] = (acc[alert.parameter] || 0) + 1;
    return acc;
  }, {});

  // Create parameter breakdown content
  const parameterBreakdown = (
    <>
      <Divider sx={{ my: 1 }} />
      <Box sx={{ display: 'flex', justifyContent: 'space-around', mt: 1 }}>
        {Object.entries(alertsByParameter).map(([param, count]) => (
          <Box key={param} sx={{ display: 'flex', alignItems: 'center', mx: 0.5 }}>
            {getParameterIcon(param)}
            <Typography variant="body2" sx={{ ml: 0.5 }}>
              {count}
            </Typography>
          </Box>
        ))}
      </Box>
    </>
  );

  // Create status info content
  const statusInfo = (
    <Typography variant="caption" color="text.secondary" display="block">
      {lastUpdated ? `Last updated: ${lastUpdated.toLocaleTimeString()}` : 'Last updated: N/A'}
    </Typography>
  );

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center">
        <Box>
          <Typography variant="h6" gutterBottom>
            Alert Status Overview
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Total Alerts: {totalAlerts}
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Active Alerts: {triggeredAlerts.length}
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Normal Alerts: {normalAlerts.length}
          </Typography>
        </Box>
        {lastUpdated && (
          <Typography variant="body2" color="text.secondary">
            Last Updated: {lastUpdated.toLocaleString()}
          </Typography>
        )}
      </Box>
    </Paper>
  );
};

export default AlertStatusOverview;
