import React from 'react';
import {
  Box,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  OutlinedInput,
  SelectChangeEvent,
  Paper
} from '@mui/material';
import { PARAMETERS } from '../../utils/alertUtils';

export interface AlertFilters {
  search: string;
  status: string[];
  parameters: string[];
}

interface AlertFiltersProps {
  filters: AlertFilters;
  onFilterChange: (filters: AlertFilters) => void;
}

const AlertFilters: React.FC<AlertFiltersProps> = ({ filters, onFilterChange }) => {
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onFilterChange({
      ...filters,
      search: e.target.value
    });
  };

  const handleStatusChange = (event: SelectChangeEvent<string[]>) => {
    const value = event.target.value;
    onFilterChange({
      ...filters,
      status: typeof value === 'string' ? value.split(',') : value,
    });
  };

  const handleParameterChange = (event: SelectChangeEvent<string[]>) => {
    const value = event.target.value;
    onFilterChange({
      ...filters,
      parameters: typeof value === 'string' ? value.split(',') : value,
    });
  };

  return (
    <Paper elevation={0} sx={{ p: 2, mb: 3, border: '1px solid', borderColor: 'divider' }}>
      <Box display="flex" flexWrap="wrap" gap={2}>
        <TextField
          label="Search alerts"
          variant="outlined"
          size="small"
          value={filters.search}
          onChange={handleSearchChange}
          sx={{ flexGrow: 1, minWidth: '200px' }}
          placeholder="Search by name or location"
        />

        <FormControl sx={{ minWidth: '150px' }} size="small">
          <InputLabel id="status-filter-label">Status</InputLabel>
          <Select
            labelId="status-filter-label"
            multiple
            value={filters.status}
            onChange={handleStatusChange}
            input={<OutlinedInput label="Status" />}
            renderValue={(selected) => (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {selected.map((value) => (
                  <Chip key={value} label={value === 'active' ? 'Active' : 'Inactive'} size="small" />
                ))}
              </Box>
            )}
          >
            <MenuItem value="active">Active</MenuItem>
            <MenuItem value="inactive">Inactive</MenuItem>
          </Select>
        </FormControl>

        <FormControl sx={{ minWidth: '200px' }} size="small">
          <InputLabel id="parameter-filter-label">Parameter</InputLabel>
          <Select
            labelId="parameter-filter-label"
            multiple
            value={filters.parameters}
            onChange={handleParameterChange}
            input={<OutlinedInput label="Parameter" />}
            renderValue={(selected) => (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {selected.map((value) => {
                  const parameter = PARAMETERS.find(p => p.value === value);
                  return (
                    <Chip 
                      key={value} 
                      label={parameter?.label || value} 
                      size="small"
                      icon={parameter?.icon ? React.cloneElement(parameter.icon, { fontSize: 'small' }) : undefined}
                    />
                  );
                })}
              </Box>
            )}
          >
            {PARAMETERS.map((param) => (
              <MenuItem key={param.value} value={param.value}>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  {param.icon}
                  <Box sx={{ ml: 1 }}>{param.label}</Box>
                </Box>
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </Box>
    </Paper>
  );
};

export default AlertFilters;
