import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  CircularProgress,
  useTheme,
  useMediaQuery
} from '@mui/material';
import { WeatherData } from '../types/weather';
import { fetchCurrentWeather } from '../services/weatherService';
import {
  WeatherSearch,
  WeatherDisplay,
  ErrorDisplay
} from '../components/Weather';

const Home: React.FC = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [weather, setWeather] = useState<WeatherData | null>(null);
  const [location, setLocation] = useState<string>('');
  const [searchLocation, setSearchLocation] = useState<{ city: string } | { lat: number; lon: number }>({ city: 'New York City' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [animateCard, setAnimateCard] = useState(false);

  useEffect(() => {
    loadWeather(searchLocation);
  }, [searchLocation]);

  const loadWeather = async (locationParam: { city: string } | { lat: number; lon: number }) => {
    setLoading(true);
    setError(null);
    try {
      const data = await fetchCurrentWeather(locationParam);
      setWeather(data);
      setAnimateCard(true);
      
      // Set the display location text
      if ('city' in locationParam) {
        setLocation(locationParam.city);
      } else {
        setLocation(`${locationParam.lat.toFixed(2)}, ${locationParam.lon.toFixed(2)}`);
      }
    } catch (err) {
      setError('Failed to load weather data. Please check the location and try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleLocationSearch = 
    (locationParam: { city: string } | { lat: number; lon: number }) => 
    setSearchLocation(locationParam);


  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setSearchLocation({ 
            lat: position.coords.latitude, 
            lon: position.coords.longitude 
          });
        },
        (error) => {
          setError('Unable to retrieve your location. Please enter it manually.');
          setLoading(false);
        }
      );
    } else {
      setError('Geolocation is not supported by your browser. Please enter your location manually.');
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Weather Dashboard
      </Typography>

      <WeatherSearch 
        initialLocation={location}
        onSearch={handleLocationSearch}
        onGetCurrentLocation={getCurrentLocation}
      />

      {loading ? (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="300px">
          <CircularProgress />
        </Box>
      ) : error ? (
        <ErrorDisplay message={error} />
      ) : weather ? (
        <WeatherDisplay weather={weather} animate={animateCard} />
      ) : null}
    </Box>
  );
};

export default Home;
