import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  CardHeader,
  Chip,
  CircularProgress,
  Divider,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  ListItemSecondaryAction,
  Alert,
  Button,
  Fade,
  Badge
} from '@mui/material';
import ThermostatIcon from '@mui/icons-material/Thermostat';
import AirIcon from '@mui/icons-material/Air';
import WaterDropIcon from '@mui/icons-material/WaterDrop';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import NotificationsOffIcon from '@mui/icons-material/NotificationsOff';
import RefreshIcon from '@mui/icons-material/Refresh';
import WarningIcon from '@mui/icons-material/Warning';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import { Alert as AlertType } from '../types/weather';
import { getAlerts } from '../services/alertService';

// Helper function to get parameter icon
const getParameterIcon = (parameter: string) => {
  switch (parameter) {
    case 'temperature':
      return <ThermostatIcon sx={{ color: '#f44336' }} />;
    case 'windSpeed':
      return <AirIcon sx={{ color: '#2196f3' }} />;
    case 'precipitation':
      return <WaterDropIcon sx={{ color: '#4caf50' }} />;
    case 'humidity':
      return <WaterDropIcon sx={{ color: '#9c27b0' }} />;
    default:
      return <ThermostatIcon />;
  }
};

// Helper function to format the condition for display
const formatCondition = (parameter: string, condition: string, threshold: number): string => {
  let unit = '';
  
  switch (parameter) {
    case 'temperature':
      unit = '°C';
      break;
    case 'windSpeed':
      unit = 'm/s';
      break;
    case 'precipitation':
    case 'humidity':
      unit = '%';
      break;
  }
  
  return `${condition} ${threshold}${unit}`;
};

const CurrentState: React.FC = () => {
  const [alerts, setAlerts] = useState<AlertType[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [lastUpdated, setLastUpdated] = useState<Date>(new Date());
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    loadAlerts();
    const interval = setInterval(loadAlerts, 30000); // Refresh every 30 seconds

    return () => clearInterval(interval);
  }, []);

  const loadAlerts = async () => {
    setLoading(true);
    setError(null);
    setFadeIn(false);
    
    try {
      const data = await getAlerts();
      setAlerts(data);
      setLastUpdated(new Date());
      setTimeout(() => setFadeIn(true), 100);
    } catch (err) {
      setError('Failed to load alerts');
    } finally {
      setLoading(false);
    }
  };

  // Stats calculation
  const triggeredAlerts = alerts.filter((alert) => alert.isTriggered);
  const activeAlerts = alerts.filter((alert) => !alert.isTriggered);
  const totalAlerts = alerts.length;
  const alertsByParameter = alerts.reduce((acc: {[key: string]: number}, alert) => {
    acc[alert.parameter] = (acc[alert.parameter] || 0) + 1;
    return acc;
  }, {});

  if (loading && alerts.length === 0) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="300px">
        <CircularProgress />
      </Box>
    );
  }

  if (error && alerts.length === 0) {
    return (
      <Alert severity="error" sx={{ mt: 2 }}>
        {error}
      </Alert>
    );
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4">
          Alert Status
        </Typography>
        <Button 
          startIcon={loading ? <CircularProgress size={20} /> : <RefreshIcon />}
          onClick={loadAlerts}
          disabled={loading}
          variant="outlined"
        >
          Refresh
        </Button>
      </Box>

      {/* Alert status overview */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card elevation={3}>
            <CardContent sx={{ textAlign: 'center' }}>
              <Badge 
                badgeContent={triggeredAlerts.length} 
                color="error"
                max={99}
                sx={{ '& .MuiBadge-badge': { fontSize: 18, height: 28, width: 28, borderRadius: 14 } }}
              >
                <NotificationsActiveIcon fontSize="large" color={triggeredAlerts.length > 0 ? "error" : "disabled"} />
              </Badge>
              <Typography variant="h5" sx={{ mt: 1 }}>
                {triggeredAlerts.length}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Triggered Alerts
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        
        <Grid item xs={12} sm={6} md={3}>
          <Card elevation={3}>
            <CardContent sx={{ textAlign: 'center' }}>
              <Badge 
                badgeContent={activeAlerts.length} 
                color="success"
                max={99}
                sx={{ '& .MuiBadge-badge': { fontSize: 18, height: 28, width: 28, borderRadius: 14 } }}
              >
                <NotificationsOffIcon fontSize="large" color={activeAlerts.length > 0 ? "success" : "disabled"} />
              </Badge>
              <Typography variant="h5" sx={{ mt: 1 }}>
                {activeAlerts.length}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Normal Alerts
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        
        <Grid item xs={12} sm={6} md={3}>
          <Card elevation={3}>
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h5" sx={{ fontWeight: 'bold', color: totalAlerts > 0 ? '#1976d2' : 'text.secondary' }}>
                {totalAlerts}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total Alerts
              </Typography>
              <Divider sx={{ my: 1 }} />
              <Box sx={{ display: 'flex', justifyContent: 'space-around', mt: 1 }}>
                {Object.entries(alertsByParameter).map(([param, count]) => (
                  <Box key={param} sx={{ display: 'flex', alignItems: 'center', mx: 0.5 }}>
                    {getParameterIcon(param)}
                    <Typography variant="body2" sx={{ ml: 0.5 }}>
                      {count}
                    </Typography>
                  </Box>
                ))}
              </Box>
            </CardContent>
          </Card>
        </Grid>
        
        <Grid item xs={12} sm={6} md={3}>
          <Card elevation={3}>
            <CardContent sx={{ textAlign: 'center' }}>
              <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                {triggeredAlerts.length > 0 ? (
                  <WarningIcon fontSize="large" color="error" />
                ) : (
                  <CheckCircleIcon fontSize="large" color="success" />
                )}
              </Box>
              <Typography variant="h6" sx={{ mt: 1, color: triggeredAlerts.length > 0 ? 'error.main' : 'success.main' }}>
                {triggeredAlerts.length > 0 ? 'Alert Conditions' : 'All Clear'}
              </Typography>
              <Typography variant="caption" color="text.secondary" display="block">
                Last updated: {lastUpdated.toLocaleTimeString()}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Triggered alerts */}
      <Fade in={fadeIn}>
        <Box>
          {triggeredAlerts.length > 0 ? (
            <Paper elevation={3} sx={{ p: 2, mb: 3, borderLeft: '4px solid #f44336' }}>
              <Typography variant="h6" sx={{ mb: 2, color: 'error.main', display: 'flex', alignItems: 'center' }}>
                <WarningIcon sx={{ mr: 1 }} />
                Active Alerts ({triggeredAlerts.length})
              </Typography>
              <List>
                {triggeredAlerts.map((alert) => (
                  <Card key={alert.id} sx={{ mb: 2, backgroundColor: 'rgba(244, 67, 54, 0.05)' }}>
                    <CardContent>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Avatar sx={{ bgcolor: 'error.main', mr: 2 }}>
                            {getParameterIcon(alert.parameter)}
                          </Avatar>
                          <Box>
                            <Typography variant="h6">{alert.name}</Typography>
                            <Typography variant="body2" color="text.secondary">
                              {alert.parameter} {formatCondition(alert.parameter, alert.condition, alert.threshold)}
                            </Typography>
                          </Box>
                        </Box>
                        <Chip 
                          label="TRIGGERED" 
                          color="error" 
                          icon={<NotificationsActiveIcon />} 
                        />
                      </Box>
                      {alert.description && (
                        <Typography variant="body2" sx={{ mt: 1, pl: 7 }}>
                          {alert.description}
                        </Typography>
                      )}
                      <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 1, pl: 7 }}>
                        Location: {alert.location.city ?? 
                        `Lat: ${alert.location.coordinates?.lat}, Lon: ${alert.location.coordinates?.lon}`}
                      </Typography>
                    </CardContent>
                  </Card>
                ))}
              </List>
            </Paper>
          ) : alerts.length > 0 ? (
            <Paper elevation={3} sx={{ p: 3, mb: 3, bgcolor: '#e8f5e9', borderLeft: '4px solid #4caf50' }}>
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
                <CheckCircleIcon color="success" sx={{ fontSize: 60, mb: 2 }} />
                <Typography variant="h5" color="success.main" align="center" gutterBottom>
                  All Clear - No Active Alerts
                </Typography>
                <Typography variant="body1" color="text.secondary" align="center">
                  None of your {alerts.length} alerts are currently triggered.
                </Typography>
              </Box>
            </Paper>
          ) : null}

          {/* Inactive alerts */}
          {activeAlerts.length > 0 && (
            <Paper elevation={3} sx={{ p: 2 }}>
              <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
                <NotificationsOffIcon sx={{ mr: 1 }} />
                Inactive Alerts ({activeAlerts.length})
              </Typography>
              <List>
                {activeAlerts.map((alert) => (
                  <Card key={alert.id} variant="outlined" sx={{ mb: 2 }}>
                    <CardContent>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Avatar sx={{ bgcolor: 'action.selected', mr: 2 }}>
                            {getParameterIcon(alert.parameter)}
                          </Avatar>
                          <Box>
                            <Typography variant="h6">{alert.name}</Typography>
                            <Typography variant="body2" color="text.secondary">
                              {alert.parameter} {formatCondition(alert.parameter, alert.condition, alert.threshold)}
                            </Typography>
                          </Box>
                        </Box>
                        <Chip 
                          label="NORMAL" 
                          color="success" 
                        />
                      </Box>
                    </CardContent>
                  </Card>
                ))}
              </List>
            </Paper>
          )}
        </Box>
      </Fade>
    </Box>
  );
};

export default CurrentState;
