import React, { useState } from 'react';
import {
  Typography,
  Paper,
  Grid,
  Button,
  TextField,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  Box,
  Switch,
  FormControlLabel
} from '@mui/material';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';

// Import from shared utils
import { PARAMETERS, CONDITIONS, DEFAULT_ALERT, getParameterUnit } from '../../utils/alertUtils';
import { CreateAlertPayload } from '../../types/alert';

// Types
interface AlertFormProps {
  onSubmit: (alert: CreateAlertPayload) => Promise<void>;
  onSuccess: () => void;
}

const AlertForm: React.FC<AlertFormProps> = ({ onSubmit, onSuccess }) => {
  const [newAlert, setNewAlert] = useState<CreateAlertPayload>({
    name: '',
    type: '',
    description: '',
    location: {
      city: ''
    },
    thresholds: {}
  });
  const [useCoordinates, setUseCoordinates] = useState(false);
  const [formErrors, setFormErrors] = useState<Record<string, string | null>>({});

  // Handle form input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    
    if (!name) return;
    
    if (name === 'lat' || name === 'lon') {
      setNewAlert({
        ...newAlert,
        location: {
          ...newAlert.location,
          coordinates: {
            ...(newAlert.location.coordinates || { lat: 0, lon: 0 }),
            [name]: Number(value)
          }
        }
      });
    } else if (name === 'city') {
      setNewAlert({
        ...newAlert,
        location: { city: value as string }
      });
    } else {
      setNewAlert({
        ...newAlert,
        [name]: value
      });
    }
    
    // Clear error when field is edited
    if (formErrors[name]) {
      setFormErrors({
        ...formErrors,
        [name]: null
      });
    }
  };

  // Toggle between city and coordinates
  const handleLocationTypeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setUseCoordinates(e.target.checked);
    if (e.target.checked) {
      setNewAlert({
        ...newAlert,
        location: { coordinates: { lat: 0, lon: 0 } }
      });
    } else {
      setNewAlert({
        ...newAlert,
        location: { city: '' }
      });
    }
  };

  // Validate form before submission
  const validateForm = () => {
    const errors: Record<string, string> = {};
    
    if (!newAlert.name.trim()) {
      errors.name = 'Name is required';
    }
    
    if (!newAlert.type.trim()) {
      errors.type = 'Type is required';
    }
    
    if (useCoordinates) {
      if (!newAlert.location?.coordinates?.lat && newAlert.location?.coordinates?.lat !== 0) {
        errors.lat = 'Latitude is required';
      } else if (newAlert.location.coordinates.lat < -90 || newAlert.location.coordinates.lat > 90) {
        errors.lat = 'Latitude must be between -90 and 90';
      }
      
      if (!newAlert.location?.coordinates?.lon && newAlert.location?.coordinates?.lon !== 0) {
        errors.lon = 'Longitude is required';
      } else if (newAlert.location.coordinates.lon < -180 || newAlert.location.coordinates.lon > 180) {
        errors.lon = 'Longitude must be between -180 and 180';
      }
    } else {
      if (!newAlert.location?.city?.trim()) {
        errors.city = 'City is required';
      }
    }
    
    if (!newAlert.userEmail.trim()) {
      errors.userEmail = 'Email is required for notifications';
    } else if (!/\S+@\S+\.\S+/.test(newAlert.userEmail)) {
      errors.userEmail = 'Email address is invalid';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Submit new alert
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    try {
      await onSubmit(newAlert);
      setNewAlert(DEFAULT_ALERT as CreateAlertPayload);
      onSuccess();
    } catch (err) {
      // Error handling is done at the parent level
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
      <Typography variant="h6" gutterBottom>
        Create New Alert
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Alert Name"
              name="name"
              value={newAlert.name}
              onChange={handleInputChange}
              error={!!formErrors.name}
              helperText={formErrors.name}
              required
            />
          </Grid>
          
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Email for Notifications"
              name="userEmail"
              type="email"
              value={newAlert.userEmail}
              onChange={handleInputChange}
              error={!!formErrors.userEmail}
              helperText={formErrors.userEmail}
              required
            />
          </Grid>
          
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Switch
                  checked={useCoordinates}
                  onChange={handleLocationTypeChange}
                  color="primary"
                />
              }
              label="Use Coordinates Instead of City"
            />
          </Grid>
          
          {useCoordinates ? (
            <>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Latitude"
                  name="lat"
                  type="number"
                  InputProps={{ inputProps: { min: -90, max: 90, step: 0.000001 } }}
                  value={newAlert.location?.coordinates?.lat || ''}
                  onChange={handleInputChange}
                  error={!!formErrors.lat}
                  helperText={formErrors.lat}
                  required
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Longitude"
                  name="lon"
                  type="number"
                  InputProps={{ inputProps: { min: -180, max: 180, step: 0.000001 } }}
                  value={newAlert.location?.coordinates?.lon || ''}
                  onChange={handleInputChange}
                  error={!!formErrors.lon}
                  helperText={formErrors.lon}
                  required
                />
              </Grid>
            </>
          ) : (
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="City"
                name="city"
                value={newAlert.location?.city || ''}
                onChange={handleInputChange}
                error={!!formErrors.city}
                helperText={formErrors.city}
                required
              />
            </Grid>
          )}
          
          <Grid item xs={12} md={4}>
            <FormControl fullWidth required>
              <InputLabel>Parameter</InputLabel>
              <Select
                name="parameter"
                value={newAlert.parameter}
                onChange={handleInputChange as any}
                label="Parameter"
              >
                {PARAMETERS.map((param) => (
                  <MenuItem key={param.value} value={param.value}>
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      {param.icon}
                      <Box sx={{ ml: 1 }}>{param.label}</Box>
                    </Box>
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          
          <Grid item xs={12} md={4}>
            <FormControl fullWidth required>
              <InputLabel>Condition</InputLabel>
              <Select
                name="condition"
                value={newAlert.condition}
                onChange={handleInputChange as any}
                label="Condition"
              >
                {CONDITIONS.map((condition) => (
                  <MenuItem key={condition.value} value={condition.value}>
                    {condition.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              label={`Threshold (${getParameterUnit(newAlert.parameter)})`}
              name="threshold"
              type="number"
              value={newAlert.threshold}
              onChange={handleInputChange}
              required
            />
          </Grid>
          
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Description (Optional)"
              name="description"
              multiline
              rows={2}
              value={newAlert.description || ''}
              onChange={handleInputChange}
            />
          </Grid>
          
          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              size="large"
              startIcon={<NotificationsActiveIcon />}
              fullWidth
            >
              Create Alert
            </Button>
          </Grid>
        </Grid>
      </form>
    </Paper>
  );
};

export default AlertForm;
