import React from 'react';
import { Grid, Divider, Box, Typography, Paper } from '@mui/material';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import NotificationsOffIcon from '@mui/icons-material/NotificationsOff';
import WarningIcon from '@mui/icons-material/Warning';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import StatusCard from '../StatusCard/StatusCard';
import { Alert } from '../../types/alert';
import { getParameterIcon } from '../../utils/alertUtils';

interface AlertStatusOverviewProps {
  alerts: Alert[];
  lastUpdated: Date;
}

const AlertStatusOverview: React.FC<AlertStatusOverviewProps> = ({ 
  alerts,
  lastUpdated
}) => {
  // Stats calculation
  const triggeredAlerts = alerts.filter((alert) => alert.isTriggered);
  const normalAlerts = alerts.filter((alert) => !alert.isTriggered);
  const totalAlerts = alerts.length;
  
  // Group alerts by parameter
  const alertsByParameter = alerts.reduce((acc: {[key: string]: number}, alert) => {
    acc[alert.parameter] = (acc[alert.parameter] || 0) + 1;
    return acc;
  }, {});

  // Create parameter breakdown content
  const parameterBreakdown = (
    <>
      <Divider sx={{ my: 1 }} />
      <Box sx={{ display: 'flex', justifyContent: 'space-around', mt: 1 }}>
        {Object.entries(alertsByParameter).map(([param, count]) => (
          <Box key={param} sx={{ display: 'flex', alignItems: 'center', mx: 0.5 }}>
            {getParameterIcon(param)}
            <Typography variant="body2" sx={{ ml: 0.5 }}>
              {count}
            </Typography>
          </Box>
        ))}
      </Box>
    </>
  );

  // Create status info content
  const statusInfo = (
    <Typography variant="caption" color="text.secondary" display="block">
      Last updated: {lastUpdated.toLocaleTimeString()}
    </Typography>
  );

  return (
    <Grid container spacing={3}>
      <Grid item xs={12} sm={6} md={3}>
        <StatusCard
          icon={<NotificationsActiveIcon />}
          value={triggeredAlerts.length}
          label="Triggered Alerts"
          badgeCount={triggeredAlerts.length}
          badgeColor="error"
        />
      </Grid>
      
      <Grid item xs={12} sm={6} md={3}>
        <StatusCard
          icon={<NotificationsOffIcon />}
          value={normalAlerts.length}
          label="Normal Alerts"
          badgeCount={normalAlerts.length}
          badgeColor="success"
        />
      </Grid>
      
      <Grid item xs={12} sm={6} md={3}>
        <StatusCard
          icon={<NotificationsActiveIcon />}
          value={totalAlerts}
          label="Total Alerts"
          iconColor={totalAlerts > 0 ? '#1976d2' : undefined}
          content={parameterBreakdown}
        />
      </Grid>
      
      <Grid item xs={12} sm={6} md={3}>
        <StatusCard
          icon={triggeredAlerts.length > 0 ? <WarningIcon /> : <CheckCircleIcon />}
          value={triggeredAlerts.length > 0 ? 'Alert Conditions' : 'All Clear'}
          label=""
          badgeColor={triggeredAlerts.length > 0 ? 'error' : 'success'}
          iconColor={triggeredAlerts.length > 0 ? '#f44336' : '#4caf50'}
          content={statusInfo}
        />
      </Grid>
    </Grid>
  );
};

export default AlertStatusOverview;
