import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  Grid,
  Button,
  TextField,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  Divider,
  CircularProgress,
  Card,
  CardContent,
  CardActions,
  Chip,
  IconButton,
  Snackbar,
  Alert as MuiAlert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Switch,
  FormControlLabel,
  Tooltip
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import RefreshIcon from '@mui/icons-material/Refresh';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import ThermometerIcon from '@mui/icons-material/Thermostat';
import AirIcon from '@mui/icons-material/Air';
import WaterDropIcon from '@mui/icons-material/WaterDrop';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import NotificationsOffIcon from '@mui/icons-material/NotificationsOff';

import { getAlerts, createAlert, deleteAlert } from '../services/alertService';
import { Alert } from '../types/weather';

// Parameters and conditions for alert creation
const PARAMETERS = [
  { value: 'temperature', label: 'Temperature', icon: <ThermometerIcon />, unit: '°C' },
  { value: 'windSpeed', label: 'Wind Speed', icon: <AirIcon />, unit: 'm/s' },
  { value: 'precipitation', label: 'Precipitation', icon: <WaterDropIcon />, unit: 'mm' },
  { value: 'humidity', label: 'Humidity', icon: <WaterDropIcon />, unit: '%' }
];

const CONDITIONS = [
  { value: '>', label: 'Greater than (>)' },
  { value: '<', label: 'Less than (<)' },
  { value: '>=', label: 'Greater than or equal to (>=)' },
  { value: '<=', label: 'Less than or equal to (<=)' },
  { value: '==', label: 'Equal to (==)' }
];

const DEFAULT_ALERT = {
  name: '',
  location: { city: '' },
  parameter: 'temperature',
  condition: '>',
  threshold: 0,
  description: '',
  userEmail: ''
};

const getParameterIcon = (parameter: string) => {
  const param = PARAMETERS.find(p => p.value === parameter);
  return param?.icon || <ThermometerIcon />;
};

const getParameterUnit = (parameter: string) => {
  const param = PARAMETERS.find(p => p.value === parameter);
  return param?.unit || '';
};

const Alerts: React.FC = () => {
  // State for alerts list
  const [alerts, setAlerts] = useState<Alert[]>([]);
  const [loading, setLoading] = useState(true);
  const [fetchError, setFetchError] = useState<string | null>(null);
  
  // State for alert form
  const [newAlert, setNewAlert] = useState<any>(DEFAULT_ALERT);
  const [useCoordinates, setUseCoordinates] = useState(false);
  const [formErrors, setFormErrors] = useState<any>({});
  
  // State for UI feedback
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [deleteConfirmOpen, setDeleteConfirmOpen] = useState(false);
  const [alertToDelete, setAlertToDelete] = useState<string | null>(null);

  // Fetch alerts on component mount
  useEffect(() => {
    loadAlerts();
  }, []);

  // Load alerts from API
  const loadAlerts = async () => {
    setLoading(true);
    setFetchError(null);
    try {
      const data = await getAlerts();
      setAlerts(data);
    } catch (err) {
      setFetchError('Failed to load alerts');
    } finally {
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    
    if (!name) return;
    
    if (name === 'lat' || name === 'lon') {
      setNewAlert({
        ...newAlert,
        location: {
          ...newAlert.location,
          coordinates: {
            ...newAlert.location.coordinates,
            [name]: value
          }
        }
      });
    } else if (name === 'city') {
      setNewAlert({
        ...newAlert,
        location: { city: value }
      });
    } else {
      setNewAlert({
        ...newAlert,
        [name]: value
      });
    }
    
    // Clear error when field is edited
    if (formErrors[name]) {
      setFormErrors({
        ...formErrors,
        [name]: null
      });
    }
  };

  // Toggle between city and coordinates
  const handleLocationTypeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setUseCoordinates(e.target.checked);
    if (e.target.checked) {
      setNewAlert({
        ...newAlert,
        location: { coordinates: { lat: 0, lon: 0 } }
      });
    } else {
      setNewAlert({
        ...newAlert,
        location: { city: '' }
      });
    }
  };

  // Validate form before submission
  const validateForm = () => {
    const errors: any = {};
    
    if (!newAlert.name.trim()) {
      errors.name = 'Name is required';
    }
    
    if (useCoordinates) {
      if (!newAlert.location?.coordinates?.lat) {
        errors.lat = 'Latitude is required';
      } else if (isNaN(Number(newAlert.location.coordinates.lat)) || 
                 Number(newAlert.location.coordinates.lat) < -90 || 
                 Number(newAlert.location.coordinates.lat) > 90) {
        errors.lat = 'Latitude must be between -90 and 90';
      }
      
      if (!newAlert.location?.coordinates?.lon) {
        errors.lon = 'Longitude is required';
      } else if (isNaN(Number(newAlert.location.coordinates.lon)) || 
                 Number(newAlert.location.coordinates.lon) < -180 || 
                 Number(newAlert.location.coordinates.lon) > 180) {
        errors.lon = 'Longitude must be between -180 and 180';
      }
    } else {
      if (!newAlert.location?.city?.trim()) {
        errors.city = 'City is required';
      }
    }
    
    if (!newAlert.userEmail.trim()) {
      errors.userEmail = 'Email is required for notifications';
    } else if (!/\S+@\S+\.\S+/.test(newAlert.userEmail)) {
      errors.userEmail = 'Email address is invalid';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Submit new alert
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    try {
      await createAlert(newAlert);
      setSuccessMessage('Alert created successfully!');
      setNewAlert(DEFAULT_ALERT);
      loadAlerts();
    } catch (err: any) {
      setErrorMessage(err.response?.data?.error || 'Failed to create alert');
    }
  };

  // Delete an alert
  const handleDelete = async (id: string) => {
    try {
      await deleteAlert(id);
      setSuccessMessage('Alert deleted successfully!');
      loadAlerts();
      setAlertToDelete(null);
      setDeleteConfirmOpen(false);
    } catch (err) {
      setErrorMessage('Failed to delete alert');
    }
  };

  // Confirm delete dialog
  const confirmDelete = (id: string) => {
    setAlertToDelete(id);
    setDeleteConfirmOpen(true);
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Weather Alerts
      </Typography>
      
      {/* Create Alert Form */}
      <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          Create New Alert
        </Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Alert Name"
                name="name"
                value={newAlert.name}
                onChange={handleInputChange}
                error={!!formErrors.name}
                helperText={formErrors.name}
                required
              />
            </Grid>
            
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Email for Notifications"
                name="userEmail"
                type="email"
                value={newAlert.userEmail}
                onChange={handleInputChange}
                error={!!formErrors.userEmail}
                helperText={formErrors.userEmail}
                required
              />
            </Grid>
            
            <Grid item xs={12}>
              <FormControlLabel
                control={
                  <Switch
                    checked={useCoordinates}
                    onChange={handleLocationTypeChange}
                    color="primary"
                  />
                }
                label="Use Coordinates Instead of City"
              />
            </Grid>
            
            {useCoordinates ? (
              <>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Latitude"
                    name="lat"
                    type="number"
                    InputProps={{ inputProps: { min: -90, max: 90, step: 0.000001 } }}
                    value={newAlert.location?.coordinates?.lat || ''}
                    onChange={handleInputChange}
                    error={!!formErrors.lat}
                    helperText={formErrors.lat}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Longitude"
                    name="lon"
                    type="number"
                    InputProps={{ inputProps: { min: -180, max: 180, step: 0.000001 } }}
                    value={newAlert.location?.coordinates?.lon || ''}
                    onChange={handleInputChange}
                    error={!!formErrors.lon}
                    helperText={formErrors.lon}
                    required
                  />
                </Grid>
              </>
            ) : (
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="City"
                  name="city"
                  value={newAlert.location?.city || ''}
                  onChange={handleInputChange}
                  error={!!formErrors.city}
                  helperText={formErrors.city}
                  required
                />
              </Grid>
            )}
            
            <Grid item xs={12} md={4}>
              <FormControl fullWidth required>
                <InputLabel>Parameter</InputLabel>
                <Select
                  name="parameter"
                  value={newAlert.parameter}
                  onChange={handleInputChange as any}
                  label="Parameter"
                >
                  {PARAMETERS.map((param) => (
                    <MenuItem key={param.value} value={param.value}>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        {param.icon}
                        <Box sx={{ ml: 1 }}>{param.label}</Box>
                      </Box>
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12} md={4}>
              <FormControl fullWidth required>
                <InputLabel>Condition</InputLabel>
                <Select
                  name="condition"
                  value={newAlert.condition}
                  onChange={handleInputChange as any}
                  label="Condition"
                >
                  {CONDITIONS.map((condition) => (
                    <MenuItem key={condition.value} value={condition.value}>
                      {condition.label}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12} md={4}>
              <TextField
                fullWidth
                label={`Threshold (${getParameterUnit(newAlert.parameter)})`}
                name="threshold"
                type="number"
                value={newAlert.threshold}
                onChange={handleInputChange}
                required
              />
            </Grid>
            
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Description (Optional)"
                name="description"
                multiline
                rows={2}
                value={newAlert.description}
                onChange={handleInputChange}
              />
            </Grid>
            
            <Grid item xs={12}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                size="large"
                startIcon={<NotificationsActiveIcon />}
                fullWidth
              >
                Create Alert
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
      
      {/* Alert List */}
      <Box sx={{ mb: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h6">
          Your Alerts ({alerts.length})
        </Typography>
        <Button 
          startIcon={<RefreshIcon />} 
          onClick={loadAlerts} 
          disabled={loading}
        >
          Refresh
        </Button>
      </Box>
      
      {loading ? (
        <Box display="flex" justifyContent="center" py={4}>
          <CircularProgress />
        </Box>
      ) : fetchError ? (
        <Paper sx={{ p: 3, bgcolor: '#ffebee' }}>
          <Typography color="error">{fetchError}</Typography>
        </Paper>
      ) : alerts.length === 0 ? (
        <Paper sx={{ p: 3, textAlign: 'center' }}>
          <Typography variant="body1">You don't have any alerts yet. Create your first one above!</Typography>
        </Paper>
      ) : (
        <Grid container spacing={3}>
          {alerts.map((alert) => (
            <Grid item xs={12} sm={6} md={4} key={alert.id}>
              <Card variant="outlined" sx={{
                borderLeft: alert.isTriggered ? '4px solid #f44336' : '4px solid #4caf50',
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}>
                <CardContent sx={{ flexGrow: 1 }}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                    <Typography variant="h6" component="div" noWrap>
                      {alert.name}
                    </Typography>
                    <Chip 
                      label={alert.isTriggered ? "Triggered" : "Normal"} 
                      color={alert.isTriggered ? "error" : "success"} 
                      size="small"
                    />
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <LocationOnIcon fontSize="small" sx={{ mr: 1 }} />
                    <Typography variant="body2" color="text.secondary">
                      {alert.resolvedLocation || 
                       (alert.location.city ?? 
                        `${alert.location.coordinates?.lat}, ${alert.location.coordinates?.lon}`)}
                    </Typography>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    {getParameterIcon(alert.parameter)}
                    <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                      {alert.parameter} {alert.condition} {alert.threshold}{getParameterUnit(alert.parameter)}
                    </Typography>
                  </Box>
                  
                  {alert.description && (
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                      {alert.description}
                    </Typography>
                  )}
                </CardContent>
                <Box sx={{ px: 2, pb: 1, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Typography variant="caption" color="text.secondary">
                    Last checked: {new Date(alert.lastChecked).toLocaleString()}
                  </Typography>
                  <IconButton 
                    aria-label="delete" 
                    color="error" 
                    onClick={() => confirmDelete(alert.id)}
                    size="small"
                  >
                    <DeleteIcon fontSize="small" />
                  </IconButton>
                </Box>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
      
      {/* Success Snackbar */}
      <Snackbar 
        open={!!successMessage} 
        autoHideDuration={6000} 
        onClose={() => setSuccessMessage(null)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <MuiAlert 
          onClose={() => setSuccessMessage(null)} 
          severity="success" 
          variant="filled"
          sx={{ width: '100%' }}
        >
          {successMessage}
        </MuiAlert>
      </Snackbar>
      
      {/* Error Snackbar */}
      <Snackbar 
        open={!!errorMessage} 
        autoHideDuration={6000} 
        onClose={() => setErrorMessage(null)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <MuiAlert 
          onClose={() => setErrorMessage(null)} 
          severity="error" 
          variant="filled"
          sx={{ width: '100%' }}
        >
          {errorMessage}
        </MuiAlert>
      </Snackbar>
      
      {/* Delete Confirmation Dialog */}
      <Dialog
        open={deleteConfirmOpen}
        onClose={() => setDeleteConfirmOpen(false)}
      >
        <DialogTitle>Delete Alert</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this alert? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteConfirmOpen(false)}>Cancel</Button>
          <Button 
            onClick={() => alertToDelete && handleDelete(alertToDelete)} 
            color="error"
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Alerts;
