import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  Grid,
  CircularProgress,
  TextField,
  InputAdornment,
  Button,
  Card,
  CardContent,
  useTheme,
  useMediaQuery,
  Fade
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import ThermostatIcon from '@mui/icons-material/Thermostat';
import AirIcon from '@mui/icons-material/Air';
import OpacityIcon from '@mui/icons-material/Opacity';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import MyLocationIcon from '@mui/icons-material/MyLocation';
import { WeatherData } from '../types/weather';
import { fetchCurrentWeather } from '../services/weatherService';

// Get a weather icon based on temperature and precipitation
const getWeatherIcon = (temperature: number, precipitation: number) => {
  if (precipitation > 50) {
    return <OpacityIcon fontSize="large" sx={{ color: '#2196f3' }} />;
  } else if (temperature > 25) {
    return <WbSunnyIcon fontSize="large" sx={{ color: '#ff9800' }} />;
  } else {
    return <WbSunnyIcon fontSize="large" sx={{ color: '#fdd835' }} />;
  }
};

const Home: React.FC = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [weather, setWeather] = useState<WeatherData | null>(null);
  const [location, setLocation] = useState<string>('');
  const [searchLocation, setSearchLocation] = useState<{ city: string } | { lat: number; lon: number }>({ city: 'New York City, NY' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [animateCard, setAnimateCard] = useState(false);

  useEffect(() => {
    loadWeather(searchLocation);
  }, [searchLocation]);

  const loadWeather = async (locationParam: { city: string } | { lat: number; lon: number }) => {
    setLoading(true);
    setError(null);
    try {
      const data = await fetchCurrentWeather(locationParam);
      setWeather(data);
      setAnimateCard(true);
      
      // Set the display location text
      if ('city' in locationParam) {
        setLocation(locationParam.city);
      } else {
        setLocation(`${locationParam.lat.toFixed(2)}, ${locationParam.lon.toFixed(2)}`);
      }
    } catch (err) {
      setError('Failed to load weather data. Please check the location and try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleLocationSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (location.trim()) {
      // Check if input looks like coordinates (lat,lon)
      const coordMatch = location.match(/^(-?\d+(\.\d+)?),\s*(-?\d+(\.\d+)?)$/);
      
      if (coordMatch) {
        const lat = parseFloat(coordMatch[1]);
        const lon = parseFloat(coordMatch[3]);
        
        if (isNaN(lat) || isNaN(lon) || lat < -90 || lat > 90 || lon < -180 || lon > 180) {
          setError('Invalid coordinates. Latitude must be between -90 and 90, longitude between -180 and 180.');
          return;
        }
        
        setSearchLocation({ lat, lon });
      } else {
        setSearchLocation({ city: location.trim() });
      }
    }
  };

  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setSearchLocation({ 
            lat: position.coords.latitude, 
            lon: position.coords.longitude 
          });
        },
        (error) => {
          setError('Unable to retrieve your location. Please enter it manually.');
          setLoading(false);
        }
      );
    } else {
      setError('Geolocation is not supported by your browser. Please enter your location manually.');
    }
  };

  // Weather metric card component
  const WeatherMetricCard = ({ icon, title, value, unit }: { icon: React.ReactNode, title: string, value: number, unit: string }) => (
    <Card elevation={2} sx={{ height: '100%' }}>
      <CardContent sx={{ textAlign: 'center' }}>
        <Box sx={{ mb: 1 }}>{icon}</Box>
        <Typography variant="h6" gutterBottom>{title}</Typography>
        <Typography variant="h4" sx={{ fontWeight: 'bold' }}>
          {value}<Typography variant="h6" component="span" sx={{ ml: 0.5 }}>{unit}</Typography>
        </Typography>
      </CardContent>
    </Card>
  );

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Weather Dashboard
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
        <form onSubmit={handleLocationSearch}>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} sm={9} md={10}>
              <TextField
                fullWidth
                label="Enter City or Coordinates (lat,lon)"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <LocationOnIcon />
                    </InputAdornment>
                  ),
                }}
                placeholder="e.g. London or 51.5074,-0.1278"
              />
            </Grid>
            <Grid item xs={6} sm={1.5} md={1}>
              <Button 
                variant="contained" 
                color="primary" 
                type="submit" 
                fullWidth
                startIcon={<SearchIcon />}
              >
                {isMobile ? '' : 'Search'}
              </Button>
            </Grid>
            <Grid item xs={6} sm={1.5} md={1}>
              <Button
                variant="outlined"
                color="primary"
                onClick={getCurrentLocation}
                fullWidth
                startIcon={<MyLocationIcon />}
              >
                {isMobile ? '' : 'Current'}
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>

      {loading ? (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="300px">
          <CircularProgress />
        </Box>
      ) : error ? (
        <Paper sx={{ p: 3, backgroundColor: '#ffebee', mb: 4 }}>
          <Typography color="error" align="center">
            {error}
          </Typography>
        </Paper>
      ) : weather ? (
        <Fade in={animateCard} timeout={500}>
          <Box>
            <Paper 
              elevation={3} 
              sx={{ 
                p: 3, 
                mb: 4,
                background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
                color: 'white'
              }}
            >
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={8} sm={10}>
                  <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold' }}>
                    Current Weather {weather.location && `in ${weather.location}`}
                  </Typography>
                  <Typography variant="body1">
                    Updated: {new Date(weather.timestamp).toLocaleString()}
                  </Typography>
                </Grid>
                <Grid item xs={4} sm={2} sx={{ textAlign: 'center' }}>
                  {getWeatherIcon(weather.temperature, weather.precipitation)}
                  <Typography variant="h4" sx={{ fontWeight: 'bold', mt: 1 }}>
                    {weather.temperature}°C
                  </Typography>
                </Grid>
              </Grid>
            </Paper>

            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} sm={4}>
                <WeatherMetricCard
                  icon={<ThermostatIcon fontSize="large" sx={{ color: '#ff5722' }} />}
                  title="Temperature"
                  value={weather.temperature}
                  unit="°C"
                />
              </Grid>
              <Grid item xs={12} sm={4}>
                <WeatherMetricCard
                  icon={<AirIcon fontSize="large" sx={{ color: '#03a9f4' }} />}
                  title="Wind Speed"
                  value={weather.windSpeed}
                  unit="m/s"
                />
              </Grid>
              <Grid item xs={12} sm={4}>
                <WeatherMetricCard
                  icon={<OpacityIcon fontSize="large" sx={{ color: '#4caf50' }} />}
                  title="Humidity"
                  value={weather.humidity}
                  unit="%"
                />
              </Grid>
            </Grid>

            <Card elevation={3}>
              <CardContent>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold' }}>
                  Precipitation Chance
                </Typography>
                <Box sx={{ 
                  height: 24, 
                  width: '100%', 
                  bgcolor: '#e0e0e0',
                  borderRadius: 1,
                  overflow: 'hidden',
                  position: 'relative'
                }}>
                  <Box sx={{ 
                    height: '100%', 
                    width: `${weather.precipitation}%`,
                    bgcolor: weather.precipitation > 60 ? '#f44336' : (weather.precipitation > 30 ? '#ff9800' : '#4caf50'),
                    transition: 'width 1s ease-in-out'
                  }} />
                  <Box sx={{ 
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center' 
                  }}>
                    <Typography variant="body2" sx={{ fontWeight: 'bold', color: 'white', textShadow: '0px 0px 2px rgba(0,0,0,0.7)' }}>
                      {weather.precipitation}%
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Box>
        </Fade>
      ) : null}
    </Box>
  );
};

export default Home;
