import React from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  IconButton,
  Tooltip
} from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';
import MenuIcon from '@mui/icons-material/Menu';
import CloudIcon from '@mui/icons-material/Cloud';
import HomeIcon from '@mui/icons-material/Home';
import NotificationsIcon from '@mui/icons-material/Notifications'; 
import WarningIcon from '@mui/icons-material/Warning';
import GitHubIcon from '@mui/icons-material/GitHub';
import { Link } from '@mui/material';

// Map icon strings to actual components
const iconMap = {
  'HomeIcon': HomeIcon,
  'NotificationsIcon': NotificationsIcon,
  'WarningIcon': WarningIcon
};

interface NavbarProps {
  isMobile: boolean;
  toggleDrawer: () => void;
  navItems: Array<{
    name: string;
    path: string;
    icon: string;
  }>;
  isActive: (path: string) => boolean;
}

const Navbar: React.FC<NavbarProps> = ({ 
  isMobile, 
  toggleDrawer, 
  navItems,
  isActive 
}) => {
  return (
    <AppBar position="sticky" elevation={0} color="primary" sx={{ 
      borderBottom: (t) => `1px solid ${t.palette.divider}`,
      backgroundImage: 'linear-gradient(90deg, #1e88e5 0%, #42a5f5 100%)'
    }}>
      <Toolbar>
        {isMobile ? (
          <>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={toggleDrawer}
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <CloudIcon sx={{ mr: 1 }} />
              <Typography variant="h6" fontWeight="bold">
                Weather Alerts
              </Typography>
            </Box>
          </>
        ) : (
          <>
            <Box sx={{ display: 'flex', alignItems: 'center', mr: 4 }}>
              <CloudIcon sx={{ mr: 1 }} />
              <Typography variant="h6" fontWeight="bold">
                Weather Alert System
              </Typography>
            </Box>
            <Box sx={{ flexGrow: 1, display: 'flex' }}>
              {navItems.map((item) => {
                const IconComponent = iconMap[item.icon as keyof typeof iconMap];
                
                return (
                  <Button 
                    key={item.name}
                    component={RouterLink}
                    to={item.path}
                    color="inherit"
                    sx={{ 
                      mx: 1, 
                      fontWeight: isActive(item.path) ? 'bold' : 'normal',
                      borderBottom: isActive(item.path) ? '3px solid white' : 'none',
                      borderRadius: 0,
                      py: 2,
                      '&:hover': {
                        backgroundColor: 'rgba(255, 255, 255, 0.1)',
                      }
                    }}
                    startIcon={<IconComponent />}
                  >
                    {item.name}
                  </Button>
                );
              })}
            </Box>
            
            <Tooltip title="View source code">
              <IconButton 
                color="inherit" 
                aria-label="github" 
                component={Link} 
                href="https://github.com/yourusername/weather-alert-system" 
                target="_blank"
              >
                <GitHubIcon />
              </IconButton>
            </Tooltip>
          </>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
